CREATE TABLE [Articles] (
    [Id] [uniqueidentifier] NOT NULL DEFAULT newid(),
    [Title] [nvarchar](max),
    [Description] [nvarchar](max),
    [DateCreated] [datetime],
    CONSTRAINT [PK_Articles] PRIMARY KEY ([Id])
)
CREATE TABLE [Sentences] (
    [Id] [uniqueidentifier] NOT NULL DEFAULT newid(),
    [Order] [int] NOT NULL,
    [DateCreated] [datetime],
    [Article_Id] [uniqueidentifier],
    CONSTRAINT [PK_Sentences] PRIMARY KEY ([Id])
)
CREATE INDEX [IX_Article_Id] ON [Sentences]([Article_Id])
CREATE TABLE [Words] (
    [Id] [uniqueidentifier] NOT NULL DEFAULT newid(),
    [Order] [int] NOT NULL,
    [Text] [nvarchar](max),
    [DateCreated] [datetime],
    [Sentence_Id] [uniqueidentifier],
    CONSTRAINT [PK_Words] PRIMARY KEY ([Id])
)
CREATE INDEX [IX_Sentence_Id] ON [Words]([Sentence_Id])
CREATE TABLE [Translations] (
    [Id] [uniqueidentifier] NOT NULL DEFAULT newid(),
    [Content] [nvarchar](max),
    [DateCreated] [datetime],
    [Discriminator] [nvarchar](128) NOT NULL,
    [Word_Id] [uniqueidentifier],
    CONSTRAINT [PK_Translations] PRIMARY KEY ([Id])
)
CREATE INDEX [IX_Word_Id] ON [Translations]([Word_Id])

